import { createSlice, createAsyncThunk } from "@reduxjs/toolkit"
import  axios from "axios"
import thunkPromiseHandler from "../../scripts/thunkPromiseHandler"

export const thunks = {
    register: createAsyncThunk("users/register", async (form, thunkAPI) => (
        thunkPromiseHandler(axios.post("/api/users/register", form), thunkAPI)
    )),
    login: createAsyncThunk("users/login", async (form, thunkAPI) => (
        thunkPromiseHandler(axios.post("/api/users/login", form), thunkAPI)
    ))
}

export const { reducer, actions } = createSlice({
    name: "users", 
    initialState : {
        isLoading : false, 
        user : null //{ email: "niro@gmail.com" }
    },
    reducers : {
        logout() {
            //define later
        }
    },
    extraReducers({ addCase }) {
        helper.methods.forEach((n) => {
            addCase(thunks[n].pending, helper.registerAnsLoginPending)
            addCase(thunks[n].fulfilled, helper.registerAnsLoginFulfilled)
            addCase(thunks[n].rejected, helper.registerAnsLoginRejected)
        })
    }
})

const helper = {
    methods : ["login", "register"],
    registerAnsLoginPending (state) {
        state.isLoading = true
    },
    registerAnsLoginFulfilled (state, { payload:user }) {
        state.isLoading = false
        state.user = user
    },
    registerAnsLoginRejected (state, { payload:errorMessage }) {
        state.isLoading = false
        alert(errorMessage || "Auth Rejected")
    }
}
