import Icon from "./Icon"
import TodoText from "./TodoText"
import { useSelector } from "react-redux"

export default function List () {

    const { isLoading, todos } = useSelector(state => state.todos)

    return (
        <ul id="list" className="list-group rounded-0 pb-3">
            {todos.map((todo) => (
                    <li 
                        key={todo.id}
                        className="list-group-item d-flex justify-content-between align-items-center">
                        <TodoText todo={todo} updateTodoIsComplete={() =>{}}/>
                        <div>
                            <Icon 
                                onClick={() => {}} //funcs.updateTodoText(todo)}
                                type="pencil-square"
                                color="warning"
                                classes={["me-3"]}
                            />
                            <Icon 
                                onClick={() => {}} //funcs.updateTodoText(todo, index)}
                                type="trash"
                                color="danger"
                            />
                        </div>
                </li>
            ))}
        </ul>
    )
}
